import React, { Component } from "react";
import Breadcumps from "../breadcumps";
import Table from "../Table";
import TableWrapper from "../TableWrappper";
import Wrapper from "../wrapper";
import { MDBBtn } from "mdbreact";

class Users extends Component {
  constructor(props) {
    super(props);
    this.state = { users: [] };
  }

  fetchData = () => {
    fetch("api/users")
      .then(res => res.json())
      .then(Users => {
        this.setState({ users: Users });
      });
  };

  componentDidMount() {
    this.fetchData();
  }

  render() {
    const ColumnData = [
      {
        label: "UserName",
        field: "UserName",
        sort: "asc",
        width: 250
      },
      {
        label: "FullNames",
        field: "FullNames",
        sort: "asc",
        width: 270
      },
      {
        label: "Email",
        field: "Email",
        sort: "asc",
        width: 200
      },
      {
        label: "Telephone",
        field: "Telephone",
        sort: "asc",
        width: 200
      },
      {
        label: "Phone",
        field: "Phone",
        sort: "asc",
        width: 200
      },
      {
        label: "action",
        field: "action",
        sort: "asc",
        width: 200
      }
    ];
    const Rowdata1 = [];
   const Rows = [...this.state.users]
    Rows.map((k,i)=> {
      const Rowdata = {
        username: k.UserName,
        FullNames: k.FullNames,
        Email: k.Email,
        Telephone: k.Telephone,
        Phone: k.Phone,
          action: <MDBBtn color ="blue" outline size="sm">Edit</MDBBtn>
      };
      Rowdata1.push(Rowdata);
    });

    return (
      <Wrapper>
        <Breadcumps tablename={"Users"} />
        <TableWrapper >
          <Table Rows={Rowdata1} columns={Rows}/>
        </TableWrapper>
      </Wrapper>
    );
  }
}

const Formdata = () => {
  return <div />;
};

export default Users;
