import React, { Component } from "react";
import NavTop from "./nav_top";
import Breadcumps from "./breadcumps";
import Footer from "./footer";
import Nav from "./nav";
import Table from "./Table";
import TableWrapper from "./TableWrappper";

class wrapper extends Component {
  constructor(props) {
    super(props);
    this.state = { users: [] };
  }

  fetchData = () => {
    fetch("api/users")
      .then(res => res.json())
      .then(Users => {
        this.setState({ users: Users });
      });
  };

  componentDidMount() {
    this.fetchData();
  }

  render() {
    const ColumnData = [
      {
        label: "UserName",
        field: "UserName",
        sort: "asc",
        width: 250
      },
      {
        label: "FullNames",
        field: "FullNames",
        sort: "asc",
        width: 270
      },
      {
        label: "Email",
        field: "Email",
        sort: "asc",
        width: 200
      },
      {
        label: "Telephone",
        field: "Telephone",
        sort: "asc",
        width: 200
      },
      {
        label: "Phone",
        field: "Phone",
        sort: "asc",
        width: 200
      }
    ];
    //const Rowdata = [];
    const rows = [...this.state.users];
    const Rowdata = {
      username: rows.UserName,
      Email: rows.Email,
      Phone: rows.Phone,
      Telephone: rows.Telephone
    };
    return (
      <div id='wrapper'>
        <Nav />
        <div id='page-wrapper' className='gray-bg'>
          <NavTop />
          <Breadcumps tablename={"Users"} />
          <TableWrapper>
            <Table Rows={Rowdata} columns={ColumnData} />
          </TableWrapper>
          <Footer />
        </div>
      </div>
    );
  }
}

const Formdata = () => {
  return <div />;
};

export default wrapper;
