import React, { Component } from "react";
import { MDBDataTable } from "mdbreact";
import NavTop from "./nav_top";
import Breadcumps from "./breadcumps";
import Footer from "./footer";
import Table from "./tables";
import Nav from "./nav";

class wrapper extends Component {
  constructor(props) {
    super(props);
    this.state = { users: [] };
  }
  fetchData = () => {
    fetch("api/users")
      .then(res => res.json())
      .then(Users => {
        this.setState({ users: Users });
      });
  };

  componentDidMount() {
    this.fetchData();
  }

  render() {
    return (
      <div id='wrapper'>
        <Nav />
        <div id='page-wrapper' className='gray-bg'>
          <NavTop />
          <Breadcumps tablename={"Users"} />
          <DatatablePage users={() => this.state.users} />
          {/* <Table users={this.state.users} onChange={() => this.fetchData} /> */}
          <Footer />
        </div>
      </div>
    );
  }
}
const DatatablePage = props => {
  const data = {
    columns: [
      {
        label: "Name",
        field: "name",
        sort: "asc",
        width: 150
      },
      {
        label: "Position",
        field: "position",
        sort: "asc",
        width: 270
      },
      {
        label: "Office",
        field: "office",
        sort: "asc",
        width: 200
      },
      {
        label: "Age",
        field: "age",
        sort: "asc",
        width: 100
      },
      {
        label: "Start date",
        field: "date",
        sort: "asc",
        width: 150
      },
      {
        label: "Salary",
        field: "salary",
        sort: "asc",
        width: 100
      }
    ],
    rows: [[props.users]]
  };

  return <MDBDataTable striped bordered hover data={data} />;
};

export default wrapper;
